import castArray from 'lodash/castArray';
import isEqual from 'lodash/isEqual';
import React, { useCallback, useMemo } from 'react';
import { Checkbox as Input, FormControlLabel } from '@material-ui/core';

const Option = ({ label, value, isChecked, check, uncheck }) => {
  const checked = useMemo(() => isChecked(value), [isChecked, value]);
  const onChange = useCallback(
    ({ target: { checked } }) => (checked ? uncheck(value) : check(value)),
    [value, check, uncheck]
  );

  return (
    <FormControlLabel
      control={<Input checked={checked} onChange={onChange} color="primary" />}
      label={label}
    />
  );
};

const Checkbox = ({ value: defaultValue, options, onChange }) => {
  const isChecked = useCallback(
    option =>
      !!castArray(defaultValue).filter(item => isEqual(item, option)).length,
    [defaultValue]
  );
  const check = useCallback(value => onChange(defaultValue.concat(value)), [
    defaultValue,
    onChange,
  ]);
  const uncheck = useCallback(
    value => onChange(defaultValue.filter(item => isEqual(item, value))),
    [defaultValue, onChange]
  );
  const renderOption = useCallback(
    (item, index) => {
      return (
        <Option
          key={index}
          {...item}
          isChecked={isChecked}
          check={check}
          uncheck={uncheck}
        />
      );
    },
    [isChecked, check, uncheck]
  );

  return options ? options.map(renderOption) : null;
};

Checkbox.propTypes = {};

Checkbox.defaultProps = {};

export default Checkbox;
