import React, { useCallback, useMemo } from 'react';
import { Checkbox as Input, FormControlLabel } from '@material-ui/core';

const Item = ({ label }) => (
  <FormControlLabel
    control={<Input checked={false} color="primary" />}
    label={label}
  />
);

// const Checkbox = ({ onChange: change, label, value, options }) => {
const Checkbox = ({ options, ...props }) => {
  /*
  const checked = useMemo(() => !!value, [value]);
  const onChange = useCallback(({ target: { checked } }) => change(checked), [
    change,
  ]);

  return (
    <FormControlLabel
      control={<Input checked={checked} onChange={onChange} color="primary" />}
      label={label}
    />
  );
  const items = useMemo(() => options || [{ label, value }], [
    options,
    label,
    value,
  ]);
  const renderItem = useCallback(
    (item, index) => <Item key={index} {...item} />,
    []
  );

  return items.map(renderItem);
  */

  const renderOption = useCallback(
    ({ label }, index) => (
      <FormControlLabel
        key={index}
        control={<Input checked={false} color="primary" />}
        label={label}
      />
    ),
    []
  );

  return options ? options.map(renderOption) : null;
};

Checkbox.propTypes = {};

Checkbox.defaultProps = {};

export default Checkbox;
